import React from 'react';
import Imdb from './Imdb'
import Stream from './Stream'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Col, Container, Row } from 'react-bootstrap';

//const apiKey = '361c5360d12fe6b74f04ebd76dfd7c4b' timdb key
const apiKey = 'nPkwzsC8PrwHhxzzZvhOFt6OU4rGNVuTFq1UQ7nS'

//generate random number
function getRandom(min,max){
    return Math.floor(Math.random()*(max-min+1))+min;
};

async function getAllMovie() {
    var page = getRandom(0, 104);
    let movieList = [];

    let listUrl = `https://api.watchmode.com/v1/list-titles/?apiKey=${apiKey}&regions=GB&page=${page}`
    var res = await fetch(listUrl);
    var dat = await res.json();
    console.log(dat)
    movieList.push(dat)

    return movieList;
}

async function getSource() {
    let url = `https://api.watchmode.com/v1/sources/?apiKey=${apiKey}&regions=GB`
    var res = await fetch(url)
    var dat = await res.json()
    return dat;
}

async function getGenre() {
    let url = `https://api.watchmode.com/v1/genres/?apiKey=${apiKey}`
    var res = await fetch(url)
    var dat = await res.json()
    return dat;
}


class Pick extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            dataList: [],
            movie: {},
            imdb_data: {},
            source: [],
            genres: [],
            selectedGenre: ''
        }
        this.handlePick = this.handlePick.bind(this);
        this.handleIMDBchange = this.handleIMDBchange.bind(this);
    }

    handleIMDBchange (key) {
        this.setState({
            imdb_data: key
        })
        //console.log('Called')
    }

    handlePick() {
        //console.log(this.state.dataList[0].titles)
        var elm = getRandom(0, this.state.dataList[0].titles.length)
        var movie = this.state.dataList[0].titles[elm]
        console.log(movie)
        this.setState({
            movie: movie
        })
    }

    componentDidMount () {
        getAllMovie().then( data =>
            this.setState({
                dataList: data
            })
        )

        getSource().then(res => 
            this.setState({
                source: res
            })
        )
    }

    render () {
        return (
            <div>
                <h1>Pick a movie for you</h1>
                <Container>
                    <Row>
                        <Col>
                            <Imdb value={this.state.movie}  onIMDBchange={this.handleIMDBchange}/>
                        </Col>
                        <Col>
                        </Col>
                    </Row>
                </Container>

                <Stream value={this.state.movie} data={this.state.source}/>
                <button onClick={this.handlePick}>Pick!</button>
                <footer className="mastfoot mt-auto">
                    <div className="inner">
                      <p>Supported regeions: GB</p>
                      <a href="https://www.watchmode.com/" target="_blank"> Streaming data powered by Watchmode.com </a>
                    </div>
                </footer>
            </div>

        )
    }
}

export default Pick
