import React from 'react';
import ReactDOM from 'react-dom';

const apiKey = '361c5360d12fe6b74f04ebd76dfd7c4b'
let movie_id = 100
let latestUrl = `https://api.themoviedb.org/3/movie/latest?api_key=${apiKey}&language=en-US`
let testURl = `https://api.themoviedb.org/3/movie/${movie_id}?api_key=${apiKey}&language=en-US`

async function getLatestId() {
    var res = await fetch(testURl)
    var dat = await res.json();
    console.log(dat)
}

class Pick extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            movie: "Let's see..."
        }
    }

    handlePick() {

        getLatestId();

        this.setState({
            movie:"Rick!!!!"
        })
    }

    render () {
        return (
            <div>{this.state.movie}
            <button onClick={()=>this.handlePick()}>Pick!</button>
            </div>
        )
    }
}

export default Pick
