import React from 'react';
import DisplayData from './Display';
import Imdb from './Imdb'
import Stream from './Stream'

//const apiKey = '361c5360d12fe6b74f04ebd76dfd7c4b' timdb key
const apiKey = 'nPkwzsC8PrwHhxzzZvhOFt6OU4rGNVuTFq1UQ7nS'

const min_movie_id = 2

//generate random number
function getRandom(min,max){
    return Math.floor(Math.random()*(max-min+1))+min;
};

async function getAllMovie() {
    let movieList = [];

    for (let index = 1; index < 2; index++) {
        let listUrl = `https://api.watchmode.com/v1/list-titles/?apiKey=${apiKey}&regions=GB&page=${index}`
        var res = await fetch(listUrl);
        var dat = await res.json();
        console.log(dat)
        movieList.push(dat)
    }

    //console.log(movieList)

    return movieList;
}

async function getSource() {
    let url = `https://api.watchmode.com/v1/sources/?apiKey=${apiKey}&regions=GB`
    var res = await fetch(url)
    var dat = await res.json()
    return dat;
}

async function getGenre() {
    let url = `https://api.watchmode.com/v1/genres/?apiKey=${apiKey}`
    var res = await fetch(url)
    var dat = await res.json()
    return dat;
}


class Pick extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            dataList: [],
            movie: {},
            imdb_data: {},
            source: [],
            genres: []
        }
        this.handlePick = this.handlePick.bind(this);
        this.handleIMDBchange = this.handleIMDBchange.bind(this);
    }

    handleIMDBchange (key) {
        this.setState({
            imdb_data: key
        })
        //console.log('Called')
    }

    handlePick() {
        //console.log(this.state.movie.length)
        var arr = getRandom(0, this.state.dataList.length) - 1;
        console.log(arr)
        if (arr >= 0) {
            var elm = getRandom(0, this.state.dataList[arr].titles.length)
            var movie = this.state.dataList[arr].titles[elm]
            console.log(movie)
            this.setState({
                movie: movie
            })

        }
    }

    componentDidMount () {
        getAllMovie().then( data =>
            this.setState({
                dataList: data
            })
        )

        getSource().then(res => 
            this.setState({
                source: res
            })
        )

        getGenre().then(res => 
            this.setState({
                genres: res
            })
        )
    }

    render () {
        return (
            <div>

                <Imdb value={this.state.movie}  onIMDBchange={this.handleIMDBchange}/> 
                <Stream value={this.state.movie} data={this.state.source}/>
                <button onClick={this.handlePick}>Pick!</button>
            </div>
        )
    }
}

export default Pick
