import React from 'react';

const apiKey = 'nPkwzsC8PrwHhxzzZvhOFt6OU4rGNVuTFq1UQ7nS'
let baseUrl = 'https://api.watchmode.com/v1/search/?apiKey={apiKey}&search_value=Ed%20Wood'

class Stream extends React.Component {
    constructor(props) {
        super(props);
    }

    componentDidUpdate(prevProps) {
        //console.log(this.props.value.imdb_id)
        var id = this.props.value.imdb_id
        if (id !== prevProps.value.imdb_id) {
            fetch(`http://www.omdbapi.com/?i=${id}&apikey=6163e7ca`)
            .then(res => res.json())
            .then(data => {
                console.log(data)
                this.setState({
                    imdbData: data
                })
                this.props.onIMDBchange(this.state.imdbData)
            })
            .catch(err => {
            	console.error(err);
            })  
        }
    }

    render () {
        return (
            <div>
                Avaialbe on:
            </div>
        )
    }
}

export default Stream;
