import React from 'react';

const apiKey = 'nPkwzsC8PrwHhxzzZvhOFt6OU4rGNVuTFq1UQ7nS'
let baseUrl = 'https://api.watchmode.com/v1/search/?apiKey={apiKey}&search_value=Ed%20Wood'

class Stream extends React.Component {
    constructor(props) {
        super(props);
        this.state ={
            stream: []
        }
    }

    componentDidUpdate(prevProps) {
        //console.log(this.props.value.imdb_id)
        var id = this.props.value.imdb_id
        if (id !== prevProps.value.imdb_id) {
            fetch(`https://api.watchmode.com/v1/search/?apiKey=${apiKey}&search_field=imdb_id&search_value=${id}`)
            .then(res => res.json())
            .then(data => {
                console.log(data.title_results[0].id)
                this.setState({
                    stream: data.title_results
                })
            })
            .catch(err => {
            	console.error(err);
            })  
        }
    }

    render () {
        return (
            <div>
                Avaialbe on:
                {this.state.stream.id}
            </div>
        )
    }
}

export default Stream;
