import React from 'react';
import ReactDOM from 'react-dom';

class Pick extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            movie: "Let's see..."
        }
    }

    render () {
        return (
            <div>{this.state.movie}
            <button onClick={()=>this.handlePick()}>Pick!</button>
            </div>
        )
    }
}

export default Pick
