import React from 'react';

const apiKey = 'nPkwzsC8PrwHhxzzZvhOFt6OU4rGNVuTFq1UQ7nS'
let baseUrl = 'https://api.watchmode.com/v1/search/?apiKey={apiKey}&search_value=Ed%20Wood'


async function getStreamData (movie_id) {
    let streams = await fetch(`https://api.watchmode.com/v1/title/${movie_id}/sources/?apiKey=${apiKey}&regions=GB`)
    .then(res => res.json())

    console.log(streams)
}

function GenreList(props) {
    let genres
    if (props.data.genres) {
        genres = props.data.map((d)=> 
            //console.log(genre.name)
            <li key={d.source_id}>
                {d.source_id}
            </li>
        )   
    }
    return (
        <div>
            Genre
            <ul>{genres }</ul>
        </div>
    )
}

class Stream extends React.Component {
    constructor(props) {
        super(props);
        this.state ={
            streamid: ""
        }
    }

    componentDidUpdate(prevProps) {
        //console.log(this.props.value.imdb_id)
        var id = this.props.value.id
        if (id !== prevProps.value.id) {
            getStreamData(id)
        }
    }

    render () {
        return (
            <div>
                Avaialbe on:

            </div>
        )
    }
}

export default Stream;
