import React from 'react';

let imgSrc = `https://www.themoviedb.org/t/p/w600_and_h900_bestv2`
let pageLink = "https://www.imdb.com/title/"

class DisplayData extends React.Component {

    constructor (props) {
        super(props);
    }

    render() {
        const genres = this.props.value.genres.map((genre, index)=>{
            <li key={index}>
                {genre}
            </li>
        })
        var imdb = this.props.data
        //console.log(genres)
        //console.log(imdb)

        return (
            <div>
                <div>
                    <h2> {this.props.value.title} {this.props.value.release_date}</h2>
                    <img src={imgSrc + this.props.value.poster_path} />
                    <p>Genre {genres}</p>
                    <a href={pageLink + this.props.value.imdb_id} target="_blank">IMDB LINK</a>
                </div>
            </div>
        )
    }
}

export default DisplayData;
