import React from 'react';

const apiKey = 'nPkwzsC8PrwHhxzzZvhOFt6OU4rGNVuTFq1UQ7nS'
let baseUrl = 'https://api.watchmode.com/v1/search/?apiKey={apiKey}&search_value=Ed%20Wood'


async function getStreamData (movie_id) {
    let id = await fetch(`https://api.watchmode.com/v1/search/?apiKey=${apiKey}&search_field=imdb_id&search_value=${movie_id}`)
    .then(res => res.json())
    .then(data => {data.title_results[0].id})
    this.id = id
    console.log(id)
}

class Stream extends React.Component {
    constructor(props) {
        super(props);
        this.state ={
            streamid: ""
        }
    }

    componentDidUpdate(prevProps) {
        //console.log(this.props.value.imdb_id)
        var id = this.props.value.imdb_id
        if (id !== prevProps.value.imdb_id) {
            getStreamData(id)
        }
    }

    render () {
        return (
            <div>
                Avaialbe on:
                {this.state.stream}
            </div>
        )
    }
}

export default Stream;
